# -*- coding: utf-8 -*-
import re
import locale
from pymystem3 import Mystem
locale.setlocale(locale.LC_ALL, '')


def Lemmatize (query):
	m = Mystem()
	lemmas = m.lemmatize(query)
	return (''.join(lemmas))

def LemmatizeQueries (queries):
	outputData = []
	for query in queries:
		d = dict([('query',query)])
		outputData.append(d)
	t = '\t'.join(queries)
	m = Mystem()
	lemmas = m.lemmatize(t)
	lemmatizedList = ''.join(lemmas).replace('\n','')
	newQueries = lemmatizedList.split('\t')
	i = 0
	for newQuery in newQueries:
		outputData[i]['lemmatizedQuery'] = newQueries[i]
		i += 1
	return outputData 

def SortWords (query):
	query = query.replace('\n','')
	wordsList = query.split(' ')
	wordsList.sort()
	return (' '.join(wordsList))

def ClearQuery(query):
	t = query.lower()
	t = re.sub(u'[^a-zA-Z0-9абвгдеёжзийклмнопрстуфхцшщыъьэючя\s.-]',' ',t)
	words = t.split(' ')
	wordsList = []
	for word in words:
		if '.' in word:
			if re.match(u'^(\d)+.(\d)+$',word):
				wordsList.append(word)
			else:
				wordsList.append(word.replace('.',' '))
		else:
			wordsList.append(word)
	t = ' '.join(wordsList)
	t = ' '.join(t.split())
	t = t.replace(' - ', '-')
	t = t.replace(' -','-')
	t = t.replace('- ','-')
	t = t.replace(u'б у',u'бу')
	t = t.replace(u'ж к',u'жк')
	t = t.replace(u'ё',u'е')
	return t

